			<view class="order-content">
				<text class="yticon icon-shouhuodizhi"></text>
				<view class="cen">
					<view class="top">
						<text class="name">{{ addressData.name }}</text>
						<text class="mobile">{{ addressData.mobile }}</text>
					</view>
					<text class="address">{{ addressData.address }} {{ addressData.area }}</text>
				</view>
				<text class="yticon icon-you"></text>
			</view>

			<image
				class="a-bg"
				src="data:image/png;base64,"
			></image>
		</navigator>

		<view class="row b-b margin-top">
			<text class="tit">数量</text>
			<input class="input" type="number" v-model="address.sum" placeholder="请输入寄件数量" placeholder-class="placeholder text-sm" />
		</view>
		<!-- <view class="cu-form-group"><textarea maxlength="-1" v-model="address.msg" :placeholder="msg" placeholder-class="placeholder text-sm"></textarea></view> -->

		<button class="add-btn" @click="confirm">提交</button>
	</view>
</template>

<script>
export default {
	data() {
		return {
			address:{
				sum:'',
				msg:''
			},
			addressData: {
				name: '点击选择取货地址',
				mobile: '',
				addressName: '',
				address: '',
				area: '',
				default: false,
			},
			msg: '备注信息，物品的大小以及重量相关信息'
		};
	},
	onLoad(option) {},
	methods: {
		switchkd(id){
			this.address.judge = id
		},
		switchChange(e) {
			this.addressData.default = e.detail;
		},
		//地图选择地址
		chooseLocation() {
			uni.chooseLocation({
				success: data => {
					this.addressData.addressName = data.name;
					this.addressData.address = data.name;
				}
			});
		},

		//提交
		confirm() {
			let data = this.address;
			if (this.addressData.name == '点击选择收货地址') {
				this.$api.msg('请选择收货位置');
				return;
			}
			if (!data.sum || data.sum<=0) {
				this.$api.msg('请输入正确寄件数量');
				return;
			}
			// 提交代取信息
			uni.navigateTo({
				url:"../money/pay"
			})
			console.log(this.address , this.addressData)
		}
	}
};
</script>

